import { useState } from 'react';

const roomTypes = [
  { id: 'single', name: 'Single Room', price: 80 },
  { id: 'double', name: 'Double Room', price: 120 },
  { id: 'triple', name: 'Triple Room', price: 160 },
];

export default function BookingForm({ onSubmit }) {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    guests: 1,
    roomType: 'single',
    checkin: '',
    checkout: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Calculate total price based on days and room type
  const selectedRoom = roomTypes.find(room => room.id === formData.roomType);
  const totalDays = formData.checkin && formData.checkout ? 
    Math.ceil((new Date(formData.checkout) - new Date(formData.checkin)) / (1000 * 60 * 60 * 24)) : 0;
  const totalPrice = selectedRoom.price * totalDays * formData.guests;

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ ...formData, totalPrice });
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded-xl shadow-md max-w-lg mx-auto space-y-4">
      <h2 className="text-2xl font-bold text-center mb-4">Room Booking</h2>

      {/* Name & Email (unchanged) */}
      <div>
        <label className="block mb-1 font-semibold">Full Name</label>
        <input
          type="text"
          name="fullName"
          value={formData.fullName}
          onChange={handleChange}
          required
          className="w-full border border-gray-300 p-2 rounded-md"
        />
      </div>

      <div>
        <label className="block mb-1 font-semibold">Email</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
          className="w-full border border-gray-300 p-2 rounded-md"
        />
      </div>

      {/* Room Type Dropdown */}
      <div>
        <label className="block mb-1 font-semibold">Room Type</label>
        <select
          name="roomType"
          value={formData.roomType}
          onChange={handleChange}
          className="w-full border border-gray-300 p-2 rounded-md"
        >
          {roomTypes.map((room) => (
            <option key={room.id} value={room.id}>
              {room.name} (${room.price}/night)
            </option>
          ))}
        </select>
      </div>

      {/* Guests (unchanged) */}
      <div>
        <label className="block mb-1 font-semibold">Guests</label>
        <input
          type="number"
          name="guests"
          min="1"
          max={formData.roomType === 'single' ? 1 : formData.roomType === 'double' ? 2 : 3}
          value={formData.guests}
          onChange={handleChange}
          className="w-full border border-gray-300 p-2 rounded-md"
        />
      </div>

      {/* Check-in/Check-out (unchanged) */}
      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="block mb-1 font-semibold">Check-in</label>
          <input
            type="date"
            name="checkin"
            value={formData.checkin}
            onChange={handleChange}
            min={new Date().toISOString().split('T')[0]}
            required
            className="w-full border border-gray-300 p-2 rounded-md"
          />
        </div>
        <div>
          <label className="block mb-1 font-semibold">Check-out</label>
          <input
            type="date"
            name="checkout"
            value={formData.checkout}
            onChange={handleChange}
            min={formData.checkin || new Date().toISOString().split('T')[0]}
            required
            className="w-full border border-gray-300 p-2 rounded-md"
          />
        </div>
      </div>

      {/* Dynamic Price Display */}
      {totalDays > 0 && (
        <div className="p-4 bg-blue-50 rounded-md">
          <p className="font-semibold">
            Total for {totalDays} night(s): <span className="text-blue-600">${totalPrice}</span>
          </p>
        </div>
      )}

      <button
        type="submit"
        className="w-full bg-blue-600 text-white p-2 rounded-md hover:bg-blue-700 transition"
      >
        Confirm Booking
      </button>
    </form>
  );
}
