import { useState } from 'react';

export default function BookingForm({ onSubmit }) {
  const [formData, setFormData] = useState({
    name: '',
    roomType: 'Single',
    guests: 1,
    checkIn: '',
    checkOut: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData); // You can console.log this for now
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded-xl shadow-md space-y-4 max-w-lg mx-auto">
      <h2 className="text-2xl font-semibold">Book Your Stay</h2>
      
      <input
        type="text"
        name="name"
        placeholder="Your Name"
        value={formData.name}
        onChange={handleChange}
        required
        className="w-full px-4 py-2 border rounded"
      />

      <select
        name="roomType"
        value={formData.roomType}
        onChange={handleChange}
        className="w-full px-4 py-2 border rounded"
      >
        <option>Single</option>
        <option>Double</option>
        <option>Triple</option>
      </select>

      <input
        type="number"
        name="guests"
        value={formData.guests}
        onChange={handleChange}
        min="1"
        max="10"
        className="w-full px-4 py-2 border rounded"
        placeholder="Guests"
      />

      <input
        type="date"
        name="checkIn"
        value={formData.checkIn}
        onChange={handleChange}
        className="w-full px-4 py-2 border rounded"
        required
      />

      <input
        type="date"
        name="checkOut"
        value={formData.checkOut}
        onChange={handleChange}
        className="w-full px-4 py-2 border rounded"
        required
      />

      <button type="submit" className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600">
        Book Now
      </button>
    </form>
  );
}
