import { useLocation, useNavigate } from 'react-router-dom';

export default function BookingSummary() {
  const location = useLocation();
  const navigate = useNavigate();
  const bookingData = location.state;

  if (!bookingData) {
    return (
      <div className="text-center mt-10 text-xl">
        No booking data found.{" "}
        <button onClick={() => navigate('/booking')} className="text-blue-600 underline">
          Go back to booking page
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-xl mx-auto mt-10 bg-white shadow-lg p-6 rounded-lg">
      <h2 className="text-2xl font-bold mb-4 text-center">Booking Summary</h2>
      <ul className="space-y-2 text-lg">
        <li><strong>Name:</strong> {bookingData.fullName}</li>
        <li><strong>Email:</strong> {bookingData.email}</li>
        <li><strong>Guests:</strong> {bookingData.guests}</li>
        <li><strong>Check-in:</strong> {bookingData.checkin}</li>
        <li><strong>Check-out:</strong> {bookingData.checkout}</li>
        <li><strong>Room Category:</strong> {bookingData.roomCategory.charAt(0).toUpperCase() + bookingData.roomCategory.slice(1)}</li>
        {bookingData.breakfast && <li><strong>Breakfast:</strong> Included</li>}
        {bookingData.dinner && <li><strong>Dinner:</strong> Included</li>}
      </ul>

      <div className="text-center mt-6">
        <button onClick={() => navigate('/booking')} className="bg-blue-600 text-white px-4 py-2 rounded-md">
          Book Another
        </button>
      </div>
    </div>
  );
}
