import { useState } from 'react';

export default function BookingForm({ onSubmit }) {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    guests: 1,
    checkin: '',
    checkout: '',
    roomCategory: 'single',
    breakfast: false,
    dinner: false,
  });

  const roomPrices = {
    single: 100,
    double: 150,
    triple: 200,
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  // Calculate total price based on selected options
  const calculateTotalPrice = () => {
    const basePrice = roomPrices[formData.roomCategory];
    let total = basePrice * formData.guests;

    if (formData.breakfast) total += 20 * formData.guests;  // Breakfast cost per guest
    if (formData.dinner) total += 30 * formData.guests;  // Dinner cost per guest

    return total;
  };

  return (
    <form onSubmit={handleSubmit} className="bg-white p-6 rounded-xl shadow-md max-w-lg mx-auto space-y-4">
      <h2 className="text-2xl font-bold text-center mb-4">Room Booking Form</h2>

      {/* Full Name */}
      <div>
        <label className="block mb-1 font-semibold">Full Name</label>
        <input
          type="text"
          name="fullName"
          value={formData.fullName}
          onChange={handleChange}
          required
          className="w-full border border-gray-300 p-2 rounded-md"
        />
      </div>

      {/* Email Address */}
      <div>
        <label className="block mb-1 font-semibold">Email Address</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
          className="w-full border border-gray-300 p-2 rounded-md"
        />
      </div>

      {/* Number of Guests */}
      <div>
        <label className="block mb-1 font-semibold">Number of Guests</label>
        <input
          type="number"
          name="guests"
          value={formData.guests}
          min="1"
          onChange={handleChange}
          required
          className="w-full border border-gray-300 p-2 rounded-md"
        />
      </div>

      {/* Room Category */}
      <div>
        <label className="block mb-1 font-semibold">Room Category</label>
        <select
          name="roomCategory"
          value={formData.roomCategory}
          onChange={handleChange}
          className="w-full border border-gray-300 p-2 rounded-md"
        >
          <option value="single">Single</option>
          <option value="double">Double</option>
          <option value="triple">Triple</option>
        </select>
      </div>

      {/* Check-in and Check-out Dates */}
      <div className="flex space-x-4">
        <div className="flex-1">
          <label className="block mb-1 font-semibold">Check-in Date</label>
          <input
            type="date"
            name="checkin"
            value={formData.checkin}
            onChange={handleChange}
            required
            className="w-full border border-gray-300 p-2 rounded-md"
          />
        </div>

        <div className="flex-1">
          <label className="block mb-1 font-semibold">Check-out Date</label>
          <input
            type="date"
            name="checkout"
            value={formData.checkout}
            onChange={handleChange}
            required
            className="w-full border border-gray-300 p-2 rounded-md"
          />
        </div>
      </div>

      {/* Additional Options */}
      <div className="space-y-2">
        <div>
          <input
            type="checkbox"
            name="breakfast"
            checked={formData.breakfast}
            onChange={handleChange}
            className="mr-2"
          />
          <label className="font-semibold">Add Breakfast ($20 per guest)</label>
        </div>
        <div>
          <input
            type="checkbox"
            name="dinner"
            checked={formData.dinner}
            onChange={handleChange}
            className="mr-2"
          />
          <label className="font-semibold">Add Dinner ($30 per guest)</label>
        </div>
      </div>

      {/* Total Price */}
      <div className="text-xl font-semibold text-center">
        Total Price: ${calculateTotalPrice()}
      </div>

      {/* Submit Button */}
      <button
        type="submit"
        className="w-full bg-blue-600 text-white p-2 rounded-md hover:bg-blue-700 transition"
      >
        Submit Booking
      </button>
    </form>
  );
}

